package com.example.hopital_numerique.controller;

import com.example.hopital_numerique.dao.ConsulltationDao;
import com.example.hopital_numerique.services.AdminServ;
import com.example.hopital_numerique.services.DoctorServ;
import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;

import java.io.IOException;

@WebServlet(name = "DoctorServlet", urlPatterns = {"/doctor/*"})
public class DoctorServlet extends HttpServlet {
    private DoctorCont doctorController;

    @Override
    public void init() throws ServletException {
        // Initialize the doctor service and controller
        ConsulltationDao consultationDao = new ConsulltationDao();
        AdminServ adminServ = new AdminServ(null, null, null, null, null); // You may need to adjust this
        DoctorServ doctorService = new DoctorServ(consultationDao, adminServ);
        this.doctorController = new DoctorCont(doctorService);
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

        String pathInfo = request.getPathInfo();

        try {
            if (pathInfo == null || pathInfo.equals("/")) {
                pathInfo = "/dashboard";
            }

            switch (pathInfo) {
                case "/dashboard":
                    doctorController.showDashboard(request, response);
                    break;
                case "/consultations":
                    doctorController.showConsultations(request, response);
                    break;
                case "/appointments":
                    doctorController.showAppointments(request, response);
                    break;
                case "/profile":
                    doctorController.showProfile(request, response);
                    break;
                default:
                    response.sendError(HttpServletResponse.SC_NOT_FOUND);
                    break;
            }
        } catch (Exception e) {
            throw new ServletException("Error processing doctor request", e);
        }
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

        String pathInfo = request.getPathInfo();

        try {
            switch (pathInfo) {
                case "/validate-consultation":
                    doctorController.validateConsultation(request, response);
                    break;
                case "/refuse-consultation":
                    doctorController.refuseConsultation(request, response);
                    break;
                case "/add-report":
                    doctorController.addConsultationReport(request, response);
                    break;
                case "/update-report":
                    doctorController.updateConsultationReport(request, response);
                    break;
                case "/update-status":
                    doctorController.updateConsultationStatus(request, response);
                    break;
                default:
                    response.sendError(HttpServletResponse.SC_NOT_FOUND);
                    break;
            }
        } catch (Exception e) {
            throw new ServletException("Error processing doctor request", e);
        }
    }
}
